mapper.sql:
,(select board_name, board_titleid, boardid from board_subtitle where useFlag='Y' )tn ,(select count(id) as count from tbl_board where useFlag= 'Y' and boardid = ${boardid}
and title like concat('%',#{q},'%')
and (levenshtein(writeid, #{q}) <=2)
)cnt where tn.board_titleid=row.titleid and tn.boardid=row.boardid order by id desc limit 10 offset ${p}

mapper.select:
select boardid
from tbl_board
where id=#{str2}
