mapper.update:
update user
set userRank = #{rank}
where userID = #{id};

update user 
set commentFlag = #{commentFlag} 
where userID = #{id};

mapper.select:
select id,boardid,title,content,regdate
from tbl_board
where writeid=#{ui};

select us.userID as id, us.userName as name, us.userEmail as email, us.regdate, us.userRank as rank
	from user us
	where us.userID= #{uin};
